Sheet sh=wb.getSheet("Sheet1")

def rowsize=sh.getRows().toInteger();
def columnsize=sh.getColumns().toInteger();
log.info "$rowsize and $columnsize"
for (def i=0;i<rowsize;i++)
{
for(def j=0;j<columnsize;j++)

 {
if(j==0)
{ email =sh.getCell(j,i).getContents()
log.info "$email"
}

if(j==1)
{ firstname =sh.getCell(j,i).getContents()
log.info "$firstname"
}
if(j==2) 
{
lastname = sh.getCell(j,i).getContents()
log.info "$lastname"
}
 }
// get TestCase
def tc = testRunner.testCase.testSuite.project.testSuites["Project Submission Functional Testing"].testCases["ADD,DELETE,GETALL,GETBYID"]

// set credential properties from the script itself
tc.testSteps["Property for add"].setPropertyValue("email", email )
tc.testSteps["Property for add"].setPropertyValue("fistname", firstname )
tc.testSteps["Property for add"].setPropertyValue("lastname", lastname )

// run test synchrouously
def runner = tc.run( null, false )

// show the test case result status
log.info "Status: $runner.status, time taken for TestCase was: $runner.timeTaken ms"
log.info "Number of test steps which ran: " + runner.results.size()

// assert that it didn't fail
assert runner.status != Status.FAILED : runner.reason

